//Breakpoints
@media (min-width: 64em) {
    //styles go here
}

//640px, 1024px, 1400px
$breakpoints-up: (
    "medium": "40em",
    "large": "64em",
    "xlarge": "87.5em",
);

//639px, 1023px, 1399px
$breakpoints-down: (
    "small": "39.9375em",
    "medium": "63.9375em",
    "large": "87.4375em",
);

@mixin breakpoint-up($size) {
    @media (min-width: map-get($breakpoints-up, $size)) {
        @content;
    }
}
@mixin breakpoint-down($size) {
    @media (max-width: map-get($breakpoints-down, $size)) {
        @content;
    }
}

sources.scss\\layouts\\_main.scss:
@use "../abstracts" as *;

main {
    @include breakpoint-up(large) {
        display: grid;
        height: 100%;
        max-width: 100%;
        grid-template-areas:
            "header hero"
            "content hero";
        grid-template-columns: minmax(31.25rem, auto) minmax(10%, 38.125rem);
        grid-template-rows: 14.625rem auto;

        background-image: url(../images/bg-pattern-desktop.svg);
        background-size: contain;
    }
}

sources.scss\\components\\_header.scss:
@use "../abstracts" as *;

.header {
    margin-bottom: 2.0069rem;
    text-align: left;

    @include breakpoint-up(large) {
        margin-bottom: 0;
    }
    &__logo {
        width: 6.25rem;
        margin-top: 1.9375rem;
        margin-left: 2rem;
        @include breakpoint-up(large) {
            width: 9.875rem;
            margin-top: 4.0569rem;
            margin-left: 10.3125rem;
        }
    }
}

sources.scss\\components\\_hero.scss:
@use "../abstracts/" as *;

.hero {
    height: 15.625rem;
    background-image: url("../images/hero-mobile.jpg");
    background-size: cover;
    background-repeat: no-repeat;
    margin-bottom: 4rem;

    @include breakpoint-up(large) {
        background-image: url("../images/hero-desktop.jpg");
        grid-area: hero;
        height: 100%;
        margin-bottom: 0;
        background-position: center;
    }
}

sources.scss\\components\\_content.scss:
@use "../abstracts/" as *;

.content {
    max-width: 30.625rem;
    margin: 0 auto;
    padding: 0 2rem;

    @include breakpoint-up(large) {
        margin: 0 10.3125rem;
        max-width: 27.75rem;
        text-align: left;
        padding: 0;
    }
    h1::first-line {
        color: $desaturated-red;
        font-weight: $light;
    }

    &__title {
        text-transform: uppercase;
        font-size: $font-lg;
        font-weight: $semi-bold;
        line-height: 2.625rem;
        letter-spacing: 0.6769rem;
        color: $dark-grayish-red;
        margin-bottom: 1rem;

        @include breakpoint-up(large) {
            font-size: $font-xl;
            line-height: 4rem;
            letter-spacing: 1.0825rem;
            margin-bottom: 2rem;
        }
    }

    &__text {
        color: $desaturated-red;
        font-size: $font-sm;
        line-height: 1.375rem;
        margin-bottom: 2rem;

        @include breakpoint-up(large) {
            font-size: $font-md;
            line-height: 1.75rem;
            margin-bottom: 2.5rem;
        }
    }

    &__form {
        position: relative;
    }

    &__email {
        font-size: $font-sm;
        line-height: 1.75rem;
        padding: 0.625rem 6.5rem 0.5rem 1.5rem;
        border-radius: 1.75rem;
        border: 0.0625rem solid $desaturated-red;
        width: 100%;
        background: none;
        color: $dark-grayish-red;
        margin-bottom: 0.5rem;
        transition: all 200ms ease-in-out;

        @include breakpoint-up(large) {
            padding: 0.875rem 9.6875rem 0.75rem 2rem;
        }

        &::placeholder {
            color: $desaturated-red;
        }
    }

    &__err-icon {
        position: absolute;
        top: 0.8125rem;
        right: 4.5rem;
        opacity: 0;
        transition: all 200ms ease-in-out;

        @include breakpoint-up(large) {
            top: 17px;
            right: 116px;
        }
    }

    &__btn {
        position: absolute;
        right: 0;
        top: 0;
        height: 3rem;
        width: 4rem;
        border-radius: 1.75rem;
        border: none;
        background: $gradient-2;
        padding: 0;

        @include breakpoint-up(large) {
            height: 3.5rem;
            width: 6.25rem;
        }

        &:hover {
            cursor: pointer;
        }
    }

    &__err-text {
        text-align: left;
        margin-left: 1.5rem;
        color: $soft-red;
        font-size: $font-xs;
        line-height: 0.8125rem;
        font-weight: $regular;
        opacity: 0;
        transition: all 200ms ease-in-out;
    }
}

.err {
    opacity: 1;
}

.err-email {
    border: 0.0625rem solid $soft-red;
}
